--TAKE THE DB OFFLINE AND ROLLBACK AND POTENTIAL ACTIONS TO BE SAFEALTER DATABASE FilePoint_Renfro SET OFFLINE WITH ROLLBACK IMMEDIATE;GO--MOVE THE FILES TO THE NEW LOCATION--THIS IS DONE ON THE FILE SYSTEM LEVEL--UPDATE THE NEW LOCATION FOR THE DATA AND LOGALTER DATABASE FilePoint_Renfro       MODIFY FILE ( NAME = FilePoint_Renfro,                     FILENAME = 'G:\MSSQL\DATA\FilePoint_Renfro.mdf');  GOALTER DATABASE FilePoint_Renfro       MODIFY FILE ( NAME = FilePoint_Renfro_log,                     FILENAME = 'G:\MSSQL\LOG\FilePoint_Renfro_1.LDF');  GO--BRING THE DB BACK ONLINEALTER DATABASE FilePoint_Renfro SET ONLINE;  --RUN A CHECK FOR THE DBUSE FilePoint_RenfroGODBCC CHECKDB