SELECT	S.name as 'Schema'		, T.name as 'Table'		, I.name as 'Index'		, DDIPS.avg_fragmentation_in_percent		, DDIPS.page_countFROM		sys.dm_db_index_physical_stats (DB_ID(), NULL, NULL, NULL, NULL) AS DDIPSINNER JOIN	sys.tables T on T.object_id = DDIPS.object_idINNER JOIN	sys.schemas S on T.schema_id = S.schema_idINNER JOIN	sys.indexes I ON I.object_id = DDIPS.object_id AND DDIPS.index_id = I.index_idWHERE		DDIPS.database_id = DB_ID()		and I.name is not null		AND DDIPS.avg_fragmentation_in_percent > 0		AND T.NAME IN ('TBL_RATE_DATE')ORDER BY	DDIPS.avg_fragmentation_in_percent desc DECLARE @SQL_REBUILD VARCHAR(MAX) = '' SELECT	@SQL_REBUILD = @SQL_REBUILD + 'ALTER INDEX '+ name +' ON '+ OBJECT_NAME(object_id) +' REBUILD ' + CHAR(13) FROM		SYS.INDEXES WHERE	OBJECT_ID IN (			OBJECT_ID('TBL_RATE_DATE')			--OBJECT_ID('TBL_FORM_RATE')			--, OBJECT_ID('TBL_JUR_DATE')			--, OBJECT_ID('TBL_RATE_DATE')			--, OBJECT_ID('TBL_CATEGORY')			--, OBJECT_ID('TBL_FIELD_MAPPING')			--, OBJECT_ID('TBL_FORM_FIELD')		)PRINT @SQL_REBUILDEXEC	(@SQL_REBUILD)SELECT	S.name as 'Schema'		, T.name as 'Table'		, I.name as 'Index'		, DDIPS.avg_fragmentation_in_percent		, DDIPS.page_countFROM		sys.dm_db_index_physical_stats (DB_ID(), NULL, NULL, NULL, NULL) AS DDIPSINNER JOIN	sys.tables T on T.object_id = DDIPS.object_idINNER JOIN	sys.schemas S on T.schema_id = S.schema_idINNER JOIN	sys.indexes I ON I.object_id = DDIPS.object_id AND DDIPS.index_id = I.index_idWHERE		DDIPS.database_id = DB_ID()		and I.name is not null		AND DDIPS.avg_fragmentation_in_percent > 0		AND T.NAME IN ('TBL_RATE_DATE')ORDER BY	DDIPS.avg_fragmentation_in_percent desc